import { React, useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import JobAdvertService from "../services/JobAdvertService";
export default function EmployerDetails() {
  const [jobAdverts, setJobAdverts] = useState({});
  let { id } = useParams();
  useEffect(() => {
    let jobAdvertService = new JobAdvertService();
    jobAdvertService
      .getByIsActiveTrueAndEmployer_Id(id)
      .then((result) => {
        setJobAdverts(result.data.data);
      });
  }, [id]);
  console.log(jobAdverts);
  return (
    <div>
        <Table color={"black"}>
            <Table.Header>
              <Table.Row>
                <Table.HeaderCell>İş Pozisyonu</Table.HeaderCell>
                <Table.HeaderCell>Şehir</Table.HeaderCell>
                <Table.HeaderCell>Açık Pozisyon</Table.HeaderCell>
                <Table.HeaderCell>Çalışma Yeri</Table.HeaderCell>
                <Table.HeaderCell>Çalışma Zamanı</Table.HeaderCell>
                <Table.HeaderCell>Detaylar</Table.HeaderCell>
              </Table.Row>
            </Table.Header>

            <Table.Body>
              {jobAdverts.map((jobAd) => (
                <Table.Row key={jobAd.id}>
                  <Table.Cell>{jobAd.jobPosition?.name}</Table.Cell>
                  <Table.Cell>{jobAd.city?.name}</Table.Cell>
                  <Table.Cell>{jobAd.openPositions}</Table.Cell>
                  <Table.Cell>{jobAd.workPlace?.name}</Table.Cell>
                  <Table.Cell>{jobAd.workTime?.name}</Table.Cell>
                  <Table.Cell>
                    <Button animated as={Link} to={`/jobads/${jobAd.id}`}>
                      <Button.Content visible>Detayları Gör</Button.Content>
                      <Button.Content hidden>
                        <Icon name="arrow right" />
                      </Button.Content>
                    </Button>
                  </Table.Cell>
                </Table.Row>
              ))}
            </Table.Body>
          </Table>
    </div>
  )
}
