import { React, useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import JobAdvertService from "../services/JobAdvertService";
export default function EmployerDetails() {
  const [jobAdverts, setJobAdverts] = useState({});
  let { companyName } = useParams();
  useEffect(() => {
    let jobAdvertService = new JobAdvertService();
    jobAdvertService
      .getByIsActiveAndEmployerCompanyName(companyName)
      .then((result) => {
        setJobAdverts(result.data.data);
      });
  }, [companyName]);
  return (
    <div>
        <Table>
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell>Şirket Adı</Table.HeaderCell>
            <Table.HeaderCell>Email</Table.HeaderCell>
            <Table.HeaderCell>Web Adres</Table.HeaderCell>
            <Table.HeaderCell>Telefon</Table.HeaderCell>
          </Table.Row>
        </Table.Header>

        <Table.Body>
          {jobAdverts.map((jobAdvert) => (
            <Table.Row key={jobAdvert.id}>
              <Table.Cell>{jobAdvert.companyName}</Table.Cell>
              <Table.Cell>{jobAdvert.email}</Table.Cell>
              <Table.Cell>{jobAdvert.webAddress}</Table.Cell>
              <Table.Cell>{jobAdvert.phoneNumber}</Table.Cell>
            </Table.Row>
          ))}
        </Table.Body>
        </Table>
    </div>
  );
}
